mapper.update:
update lakerhome.User
set password = #{password,jdbcType=VARCHAR},
    tel = #{tel,jdbcType=VARCHAR}
where email = #{email,jdbcType=VARCHAR}

mapper.select:
select lakerhome.User.email, password, tel
from lakerhome.User
where email = #{email,jdbcType=VARCHAR}

select lakerhome.User.email, password, tel
from lakerhome.User
where email = #{email,jdbcType=VARCHAR}
and password = #{password,jdbcType=VARCHAR}
